var sessionId = $('Edit Fields3').item.json.token;
var userId = $('Edit Fields3').item.json.userId;

var messages = $('Get chat Memory').item.json.messages;
var data = { userId, sessionId, messages };

// --- KONVERSI KE FORMAT OPENAI ---
var newMessages = [];
messages.forEach((item)=>{
  if(item.system){
    newMessages.push({ role:"system", content: item.system });
  } 
  if(item.human){
    newMessages.push({ role:"user", content: item.human });
  }
  if(item.ai){
    newMessages.push({ role:"assistant", content: item.ai });
  }
  if(item.role){ 
    newMessages.push({ role: item.role, content: item.content });
  }
});

// Batasi riwayat
if (newMessages.length > 20) {
  newMessages = newMessages.slice(-20);
}

const systemMessage = $json.systemMessage;

// --- CEK apakah pesan system sudah ada (keyword saja) ---
const alreadyExists = newMessages.some(
  m => m.role === "system" && (
    m.content.includes("The following is the user's project workspace") || 
    m.content.includes("You are an AI Assistant")
  )
);

if (systemMessage && !alreadyExists) {
  newMessages.push({ role: "system", content: systemMessage });
}

// --- FILTER DUPLIKAT SYSTEM MESSAGE ---
function ensureSingleSystemMessage(messages, keyword) {
  const found = messages.filter(m => m.role === "system" && m.content.includes(keyword));
  if (found.length > 1) {
    const lastOne = found[found.length - 1];
    messages = messages.filter(m => !(m.role === "system" && m.content.includes(keyword)));
    messages.push(lastOne);
  }
  return messages;
}

newMessages = ensureSingleSystemMessage(newMessages, "The following is the user's project workspace");
newMessages = ensureSingleSystemMessage(newMessages, "You are an AI Assistant");



// Tambah pesan user terakhir
newMessages.push({ role: "user", content: $('Edit Fields3').item.json.message });

data.messages = messages;
var data = {
  data,
  openai: { 
    json: {
      model: "gpt-4o-mini",
      messages: newMessages,
      max_tokens: 10000
    }
  }
};

return data;

