var messages = $json.messages || [];

// Batasi riwayat pesan
if (messages.length > 20) {
  messages = messages.slice(-20);
}

var newMessages = [];
var systemMessages = 0;

messages.forEach((item) => {
  // Hitung pesan system yang mengandung teks tertentu
  if (item.system && item.system.includes("You are an AI Assistant designed specifically to help developers")) {
    systemMessages++;
  }

  if (item.human) {
    newMessages.push({
      role: "user",
      content: item.human
    });
  }
  if (item.ai) {
    newMessages.push({
      role: "assistant",
      content: item.ai
    });
  }
});

var data = {
  systemCount: systemMessages,
  messages: newMessages
};

return data;

